import structlog
from akoyaapisv240.logging.sdk_logger import AbstractLogger


class CustomLogger(AbstractLogger):

    def __init__(self):
        structlog.configure(
            processors=[
                structlog.processors.TimeStamper(fmt="iso"),
                structlog.processors.JSONRenderer(),
            ]
        )
        self._logger = structlog.get_logger()

    def log(self, level, message, params):
        self._logger.log(level, message, *params.values(), **params)

client = Akoyaapisv240Client(
    logging_configuration=LoggingConfiguration(
        logger=CustomLogger()
    )
)

